import * as THREE from 'three'
import gsap from 'gsap'
import './style.scss'
import woodColorImgSrc from './textures/WoodFloor044_1K_Color.jpg'
import woodAmbientImgSrc from './textures/WoodFloor044_1K_AmbientOcclusion.jpg'
import woodMetalImgSrc from './textures/WoodFloor044_1K_Metalness.jpg'
import woodNormalImgSrc from './textures/WoodFloor044_1K_Normal.jpg'
import woodRoughnessImgSrc from './textures/WoodFloor044_1K_Roughness.jpg'
import woodDisplaceImgSrc from './textures/WoodFloor044_1K_Displacement.jpg'

import * as dat from 'dat.gui'
const gui = new dat.GUI()

const loadingManager = new THREE.LoadingManager()
const textureLoader = new THREE.TextureLoader( loadingManager )

const woodColorTexture = textureLoader.load( woodColorImgSrc )
const woodAmbientTexture = textureLoader.load( woodAmbientImgSrc )
const woodMetalTexture = textureLoader.load( woodMetalImgSrc )
const woodNormalTexture = textureLoader.load( woodNormalImgSrc )
const woodRoughnessTexture = textureLoader.load( woodRoughnessImgSrc )
const woodDisplaceTexture = textureLoader.load( woodDisplaceImgSrc )

woodColorTexture.minFilter = THREE.NearestFilter

const size = {
	width: window.innerWidth,
	height: window.innerHeight
}

const cursor = {
	x: 0,
	y: 0
}

window.addEventListener( 'mousemove', e => {
	cursor.x = e.clientX / size.width - 0.5
	cursor.y = e.clientY / size.height - 0.5
} )

// Scene
const scene = new THREE.Scene()

// Camera
const camera = new THREE.PerspectiveCamera( 75, size.width / size.height, 0.1, 1000 )

const renderer = new THREE.WebGLRenderer()
renderer.setSize( size.width, size.height )
renderer.setPixelRatio( Math.min( window.devicePixelRatio, 3 ) )

document.body.appendChild( renderer.domElement )

// Objects
const parameters = {
	color: 0xff0000
}

/**
 * Font convert Facetype.js
 */
const fontLoader = new THREE.FontLoader()
fontLoader.load(
	'./fonts/Roboto.json',
	font => {
		const textGeometry = new THREE.TextBufferGeometry(
			'NOVUS', {
				font: font,
				size: 0.5,
				height: 0.2,
				curveSegments: 12,
				bevelEnabled: true,
				bevelThickness: 0.03,
				bevelSize: 0.02,
				bevelOffset: 0,
				bevelSegments: 5
			}
		)

		const textMaterial = new THREE.MeshBasicMaterial()
		const text = new THREE.Mesh( textGeometry, textMaterial )
		scene.add( text )
	},
	xhr => console.log( xhr.loaded / xhr.total * 100 + '%' ),
	err => console.log( err )
)

const geo = new THREE.SphereBufferGeometry( 1.3, 16, 16 )
// const material = new THREE.MeshBasicMaterial( {
// 	map: woodColorTexture
// } )
const material = new THREE.MeshStandardMaterial( {
	metalness: 0.45,
	roughness: 0.65,
	map: woodColorTexture,
	aoMap: woodAmbientTexture,
	normalMap: woodNormalTexture,
	roughnessMap: woodRoughnessTexture,
	// displacementMap: woodDisplaceTexture,
	metalnessMap: woodMetalTexture,
} )

const box = new THREE.Mesh( geo, material )
scene.add( box )
box.geometry.setAttribute( 
	'uv2',
	new THREE.BufferAttribute( box.geometry.attributes.uv.array, 2 )
)

/**
 * Light
 */
const light = new THREE.PointLight( 0xffffff, 1.3 )
light.position.x = 2
light.position.y = 3
light.position.z = 4
scene.add( light )

camera.position.z = 3

gui.add( material, 'metalness' ).min( 0 ).max( 1 ).step( 0.001 )

const animate = () => {
	requestAnimationFrame( animate )
	// camera.position.x = cursor.x * 10
	// camera.position.y = cursor.y * 10

	// box.rotation.x += 0.01
	box.rotation.y += 0.005

	camera.lookAt( box.position )

	renderer.render( scene, camera )
}
animate()

window.addEventListener( 'resize', () => {
	size.width = window.innerWidth
	size.height = window.innerHeight

	renderer.setSize( size.width, size.height )

	camera.aspect = size.width / size.height
	camera.updateProjectionMatrix()
} )

window.addEventListener( 'dblclick', () => {
	const canvas = document.querySelector( 'canvas' )
	const isFullScreen = document.fullscreenElement || document.webkitFullscreenElement
	isFullScreen ? ( document.webkitExitFullscreen ? document.webkitExitFullscreen() : document.exitFullscreen() )
				: ( canvas.webkitRequestFullscreen ? canvas.webkitRequestFullscreen() : canvas.requestFullscreen() )
} )
